package com.dzz.medical.config.web.security;import java.io.IOException;import javax.servlet.ServletException;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.springframework.security.core.AuthenticationException;import org.springframework.security.web.authentication.AuthenticationFailureHandler;/** * 认证失败处理 * @author dzz * @since  2017年06月29 下午3:40 * @version  1.0.0 */public class MyAuthenticationFailureHandler implements AuthenticationFailureHandler {    @Override    public void onAuthenticationFailure(HttpServletRequest request,                                        HttpServletResponse response,                                        AuthenticationException e) throws IOException, ServletException {        request.getRequestDispatcher("/loginFailure?error="+e.getMessage()).forward(request,response);    }}