package com.dzz.medical.config.web.security;import java.io.Serializable;import java.util.Collection;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.userdetails.User;/** * @author dzz * @since  2017年06月26 上午9:37 * @version  1.0.0 */public class SelfUserDetails extends User implements Serializable{    private static final long serialVersionUID= -6774935433093842478L;    /*用户状态*/    private Integer status;    /*用户Id*/    private String userId;    public Integer getStatus() {        return status;    }    public void setStatus(Integer status) {        this.status = status;    }    public String getUserId() {        return userId;    }    public void setUserId(String userId) {        this.userId = userId;    }    /**     * 构造用户实体对象     * @param username 用户名     * @param password 密码     * @param authorities 授权信息     */    public SelfUserDetails(String username, String password,            Collection<? extends GrantedAuthority> authorities) {        super(username, password, authorities);    }    /**     * 构造用户实体对象     * @param username 用户名     * @param password 密码     * @param enabled 可用     * @param accountNonExpired 过期     * @param credentialsNonExpired 证书过期     * @param accountNonLocked 是否锁定     * @param authorities 授权信息     */    public SelfUserDetails(String username, String password,            boolean enabled, boolean accountNonExpired, boolean credentialsNonExpired,            boolean accountNonLocked, Collection<? extends GrantedAuthority> authorities) {        super(username, password, enabled, accountNonExpired, credentialsNonExpired,                accountNonLocked, authorities);    }}